INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2021, 'Todas as áreas', NULL, 'media', 'ENARE 2021 - Objetiva | R1  A   atenção primária à saúde tem grande potencial para estimular a prática de atividade  físic', '', 'Várias doenças apresentam melhora com a prática de atividade física, como diabetes melito e hipertensão arterial, porém outras, como osteoporose e câncer, não são afetadas por essa prátic', 'Os benefícios da atividade física são sustentados, ou seja, mesmo com a sua interrupção,  seus efeitos são permanentes.', 'Não é necessária qualquer avaliação médica na atenção primária previamente à prática  de   atividade física, sendo que a imposição dessa condição diminui a aderência e di fi culta o início da prática de exercícios.', NULL, 'A');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2021, 'Todas as áreas', NULL, 'media', 'ENARE 2021 - Objetiva | R1  Senhor   José, 70 anos, IMC 38 kg/m², sedentário, hipertenso (controlado com medicação),  sofreu   um acidente vascular encefálico (AVE) há 9 meses, apresentando como sequela redução da força em dimídio esquerdo. Desde o AVE, tem caminhado com di fi culdade e refere perda de equilíbrio e algumas quedas. Nega alterações na sensibilidad', '', 'O senhor José pode ser encaminhado à   fi sioterapia, mas apenas para controle da   dor  lombar, pois não há benefícios na realização de   fi sioterapia após 6 meses de AV', 'O senhor José pode ser encaminhado à   fi sioterapia, podendo obter como benefícios melhora da dor, do equilíbrio, fortalecimento muscular e ganho de autonomi', 'O   senhor   José   pode   ser   encaminhado   à   fi sioterapia,   porém   isso   apenas   evitará   a progressão do caso, não sendo possível a sua melhori', 'O senhor José não deve ser encaminhado à   fi sioterapia, pois, antes disso, é necessário  que deixe de ser sedentário e inicie a prática de caminhadas.', 'A');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2021, 'Todas as áreas', NULL, 'media', 'ENARE 2021 - Objetiva | R1  Em   relação   à   Saúde   do   Trabalhador   e   à   Comunicação   de   Acidente   de   Trabalho   (CAT),  assinale a alternativa corret', 'Medway - ENARE - 2021   Páginas 39/42', 'De fi ne-se como doença pro fi ssional um subtipo de doença ocupacional que é resultado do  exercício   de uma determinada pro fi ssão, prescindindo para sua comprovação o nexo causal, bastando a comprovação do exercício da atividad', 'De fi ne-se   como   acidente   de   trajeto   aquele   ocorrido   durante   o   deslocamento   do  trabalhador dentro do ambiente da empres', 'Apesar do sigilo pro fi ssional, caso a empresa solicite, o médico pode incluir o diagnóstico literal ou CID-10 no atestado do paciente, sem a necessidade da aprovação do trabalhador.', 'O acidente ocorrido com o trabalhador fora do local e do horário de trabalho não pode   ser  equiparado   ao acidente de trabalho, mesmo na execução de ordem ou na realização de  serviço sob autoridade da empres', 'A');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2021, 'Todas as áreas', NULL, 'media', 'ENARE 2021 - Objetiva | R1  Sobre   os indicadores de saúde e as internações por condições sensíveis à atenção primária (ICSAP), assinale a alternativa corret', 'A lista das ICSAP é de fi nida internacionalmente, sendo a mesma nos mais diversos países  do mundo.', 'As ICSAP variam conforme a idade, sendo que, nos extremos de idade, as hospitalizações por ICSAP tendem a diminuir.', 'A associação entre ICSAP e piores condições socioeconômicas é consistente, porém seu impacto não é muito importante, de acordo com estudos.', 'Nos municípios com alta cobertura da Estratégia da Saúde da Família (ESF), os estudos  demonstram redução signi fi cativa das ICSAP.', 'A lista das ICSAP não inclui doenças imunizáveis.', 'A');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2021, 'Todas as áreas', NULL, 'media', 'ENARE 2021 - Objetiva | R1  Referente à terapia comunitária e grupos na atenção primária à saúde (APS), assinale a  alternativa corret', 'Também   é importante um acompanhamento paralelo com os pais, sendo de preferência individual.', 'Na   construção   de   grupos   de   crianças,   é   importante   a   manutenção   de   critérios   de homogeneidade   no   que   diz   respeito   à   faixa   etária   e   ao   tipo   de   patologi', 'Os grupos de educação em saúde devem, preferencialmente, ser focados no facilitador, pois,   além   deste   deter   mais   conhecimento,   é   uma   forma   de   evitar   divagações  desnecessárias e melhorar a e fi ciência do grupo.', 'A   terapia   comunitária   dá   ênfase   em   trazer   para   a   comunidade   o   saber   cientí fi co  adequado, com foco nessas orientações, tendo pouco espaço para autonomia dos membros.', NULL, 'A');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2022, 'Todas as áreas', NULL, 'media', 'ENARE 2022 - Objetiva | R1  Um paciente com dor abdominal é admitido no pronto atendimento e é encaminhado para  avaliação médica Assinale a alternativa correta quanto ao exame desse pacient', 'Dor no ombro esquerdo quando em decúbito dorsal após pressão colocada no abdome  superior esquerdo é denominada sinal de Carnett.', 'Pacientes com irritação peritoneal tipicamente mudam continuamente de posição no leito  enquanto tentam achar uma posição que reduza seu desconforto.', 'O   médico   deve   se   posicionar   à   esquerda   do   paciente   e   iniciar   o   exame   físico   pela palpitação do abdom', 'O quadro de íleo paralitico caracteriza-se por ruídos abdominais "metálicos" em tom alto,  que tendem a ocorrer em salvas e estão associados à dor e distensão.', NULL, 'A');
