INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2021, 'Nefrologia', NULL, 'media', 'Paciente com SIADH é atendido no Hospital com quadro de hiponatremia moderada crônica. Sobre essa condição e seu tratamento, é correto afirmar que', 'O tratamento deve ser realizado com infusão rápida de soro glicosado 5%.', 'A correção lenta do sódio pode gerar mielinólise pontina central.', 'O índice de aumento do sódio sérico deve ser de no mínimo 12mEq/kg por dia.', 'Na hiponatremia prolongada, também pode haver depleção de potássio com necessidade de reposição.', 'A taxa de correção do sódio deve ser de pelo menos 0,5 mEq/L por hora para evitar mielinólise pontina.', 'D');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2021, 'Medicina intensiva', NULL, 'dificil', 'Referente ao choque hemorrágico e seu tratamento, assinale a alternativa correta.', 'Na maioria dos casos, é necessária intervenção cirúrgica imediata.', 'Pacientes adultos com hipotensão, mesmo após reposição de 500mL de solução eletrolítica, precisam de transfusão sanguínea.', 'O tratamento do choque hemorrágico pode gerar síndrome compartimental abdominal.', 'A reanimação no choque deve ser feita com infusão de coloides em bólus.', 'A coagulopatia é comum nos pacientes que precisam de transfusão maciça, sendo necessária a infusão de anticoagulantes.', 'C');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2021, 'Cirurgia', NULL, 'facil', 'Em relação às fases e processos da cicatrização, é correto afirmar que', 'há, na fase inflamatória, secreção de citocinas e migração de células para a ferida por quimiotaxia.', 'a adesão das plaquetas ao endotélio é mediada principalmente pelas prostaglandinas.', 'ocorrem, na fase de maturação da ferida, principalmente, a angiogênese e a formação de tecido de granulação.', 'a fase de proliferação se inicia após a fase de maturação, com formação de arranjos de colágeno.', 'os fibroblastos começam a produzir colágeno na fase de maturação.', 'C');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2021, 'Ginecologia', NULL, 'media', 'Uma paciente jovem com câncer de colo uterino será submetida a uma histerectomia radical videolaroscópica. Qual seria a profilaxia antimicrobiana mais recomendada?', 'Ceftriaxona 1g e Metronidazol 500mg endovenosos após a incisão cirúrgica.', 'Cefazolina 1-2g endovenosa na indução anestésica.', 'Ciprofloxacino 500mg endovenoso na indução anestésica.', 'Metronidazol 500mg e Cefazolina 1-2g endovenosos na indução anestésica.', 'Cefoxetina 1-2g e Metronidazol 500mg endovenosos na indução anestésica.', 'B');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2021, 'Cirurgia', NULL, 'facil', 'Considerando os fios cirúrgicos utilizados para sutura, assinale a alternativa correta.', 'O fio de PDS é monofilamentar e inabsorvível.', 'O fio de categute é um dos que causam menor reação tecidual.', 'O fio de Vicryl permanece com sua força tênsil em mais de 50% em 5 semanas.', 'O fio de Monocryl é monofilamentar e produz reação tecidual mínima.', 'O fio de Prolene é multifilamentar e absorvível.', 'C');
