INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2021, 'Clínica Médica', NULL, 'media', 'Paciente com SIADH (Síndrome de Liberação Inadequada de Hormônio Antidiurético) é atendido no Hospital com quadro de hiponatremia moderada crônica. Sobre essa condição e seu tratamento, é correto afirmar que', 'O tratamento deve ser realizado com infusão rápida de soro glicosado 5%.', 'A correção lenta do sódio pode gerar mielinólise pontina central.', 'O índice de aumento do sódio sérico deve ser de no mínimo 12mEq/kg por dia.', 'Na hiponatremia prolongada, também pode haver depleção de potássio com necessidade de reposição.', 'A taxa de correção do sódio deve ser de pelo menos 0,5 mEq/L por hora para evitar mielinólise pontina.', 'D');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2021, 'Clínica Médica', NULL, 'alta', 'Referente ao choque hemorrágico e seu tratamento, assinale a alternativa correta.', 'Na maioria dos casos, é necessária intervenção cirúrgica imediata.', 'Pacientes adultos com hipotensão, mesmo após reposição de 500mL de solução eletrolítica, precisam de transfusão sanguínea.', 'O tratamento do choque hemorrágico pode gerar síndrome compartimental abdominal.', 'A reanimação no choque deve ser feita com infusão de coloides em bólus.', 'A coagulopatia é comum nos pacientes que precisam de transfusão maciça, sendo necessária a infusão de anticoagulantes.', 'C');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2021, 'Cirurgia', NULL, 'media', 'Em relação às fases e processos da cicatrização, é correto afirmar que', 'há, na fase inflamatória, secreção de citocinas e migração de células para a ferida por quimiotaxia.', 'a adesão das plaquetas ao endotélio é mediada principalmente pelas prostaglandinas.', 'ocorrem, na fase de maturação da ferida, principalmente, a angiogênese e a formação de tecido de granulação.', 'a fase de proliferação se inicia após a fase de maturação, com formação de arranjos de colágeno.', NULL, 'A');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2021, 'Endocrinologia', NULL, 'media', 'Sobre a regulação hormonal da homeostase do cálcio, é correto afirmar que', 'A calcitonina é o principal hormônio regulador do cálcio sérico.', 'O cálcio é essencial para a coagulação sanguínea e a excitabilidade neuromuscular.', 'A paratireoide produz o hormônio calcitriol, que aumenta a absorção de cálcio nos rins.', 'O aumento da fosfatemia inibe a secreção de paratormônio.', 'A vitamina D aumenta a absorção intestinal de cálcio.', 'E');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2021, 'Gastroenterologia', NULL, 'media', 'Paciente, 50 anos, masculino, comparece à consulta para avaliação de rotina. Apresenta quadro de dispepsia e sensação de plenitude pós-prandial. Há perda ponderal de 10 kg nos últimos 60 dias. Assinale a alternativa correta sobre a investigação desse caso.', 'Solicitar endoscopia digestiva alta com biópsia é a conduta inicial mais adequada.', 'A pesquisa de Helicobacter pylori deve ser o primeiro exame solicitado.', 'A ultrassonografia de abdome deve ser priorizada em relação a exames endoscópicos.', 'A investigação deve focar em repetir exames de sangue antes de procedimentos invasivos.', 'Tratar inicialmente com inibidores de bomba de prótons é a melhor estratégia.', 'A');
