INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Cirurgia', 'Oncologia', 'media', 'Em relação ao risco de contaminação, podemos classificar uma cirurgia eletiva de câncer de estômago com abordagem controlada da cavidade gástrica como', 'limpa', 'contaminada', 'potencialmente contaminada', 'suja', 'infectada', 'C');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Cirurgia', 'Coloproctologia', 'media', 'Uma paciente de 32 anos teve, recentemente, diagnóstico de PAF (Polipose Adenomatosa Familiar) e possui aversão a cirurgias. Qual é a orientação correta a ser feita nesse caso?', 'Orientar que a paciente possui uma síndrome genética de herança autossômica recessiva que dificilmente será transmitida a outras gerações.', 'Orientar que a paciente provavelmente precisará de intervenção cirúrgica já que tem grandes chances de desenvolver um câncer colorretal em algum momento da sua vida.', 'Orientar a paciente que o manejo da Polipose geralmente é feito por meio de acompanhamento clínico, com colonoscopia a cada 5 anos até os 60 anos deixando para comentar quando preciso sobre a possível necessidade de cirurgia.', 'Orientar que essa patologia é caracterizada pela existência de alguns pólipos na região do reto que podem ser acompanhado e ressecados por colonoscopia, já que a paciente possui aversão a cirurgias.', 'Orientar que essa patologia ocorre por uma mutação no gene BRCA1 e portanto, deve ser indicada também adenomastectomia promatica pelo alto risco de câncer de mama.', 'B');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Cirurgia', 'Cardiovascular', 'facil', 'Um paciente será submetido a uma cirurgia cardíaca eletiva. Qual antibiótico deve ser preferencialmente utilizado para a profilaxia antimicrobiana cirúrgica?', 'Cefazolina', 'Metronidazol', 'Ceftriaxona', 'Cefepima', 'Ciprofloxacino', 'A');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Cirurgia', 'Gastroenterologia', 'media', 'Assinale a alternativa correta quanto as características das doenças inflamatórias intestinais - doença de Crohn e retocolite ulcerativa', 'A retocolite ulcerativa tem sua maior incidência em pacientes de 50 anos.', 'A retocolite ulcerativa é uma doença que geralmente acomete mucosa, submucosa e muscular do cólon.', 'A retocolite ulcerativa pode estar associada a manifestações e extraintestinais, como colangite esclerosante, e geralmente é agravada pelo tabagismo.', 'Tanto a retocolite ulcerativa como a doença de Crohn são mais comuns entre mulheres que usam contraceptivos orais em comparação com aquelas não usam.', 'O tabagismo atua como efeito protetor na doença de Crohn.', 'D');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Cirurgia', 'Cabeça e Pescoço', 'media', 'Um paciente será submetido a uma tireoidectomia total. Qual das alternativas a seguir apresenta uma complicação que pode ocorrer em consequência desse tipo de cirurgia e sua justificativa?', 'Hiperparatireoidismo por lesão das paratireoides.', 'Paralisia de prega vocal por lesão do nervo laríngeo recorrente.', 'Hipertireoidismo por liberação de grande quantidade de hormônios tireoideanos.', 'Rouquidão lesão do nervo glossofaríngeo.', 'Hipercalcemia por paratireoidectomia acidental.', 'B');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Cirurgia', 'Ortopedia', 'media', 'Um paciente, 20 anos, teve fratura exposta de tibia grau I de Gustillo-Anderson. Qual seria a forma mais correta de realizar o primeiro atendimento hospitalar desse paciente?', 'Profilaxia com Ceftriaxona e vacina antitetânica seguida de lavagem e desbridamento cirúrgico', 'Iniciar antibiótico de forma terapêutica com Ciprofloxacino, realizar vacina antirrábica e seguir com lavagem e redução local da fratura.', 'Redução da fratura após analgesia, lavagem e antibioticoterapia profilática com Vancomicina durante a indução anestésica.', 'Profilaxia com Cefalotina e vacina antitetânica seguida de lavagem e desbridamento cirúrgico/redução da fratura.', 'Analgesia, profilaxia com vacina antitetânica e Ciprofloxacino e redução imediata da fratura seguidas de lavagem e desbridamento cirúrgico.', 'D');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Cirurgia', 'Oncologia', 'media', 'Assinale a alternativa que corresponde à sequência correta de marcadores mais relacionados aos seguintes tipos de câncer, nesta ordem: câncer de pâncreas / câncer de ovário / câncer colorretal / hepatocarcinoma.', 'Ca 125 / CEA / Ca 19-9 / alfafetoproteina.', 'Ca 19-9 / Ca 15-3 / CEA / LDH.', 'CEA / Ca 125 / CEA / LDH.', 'Ca 19-9 / Ca 15-3 / CEA / alfafetoproteina.', 'Ca 19-9 / Ca 125 / CEA / alfafetoproteina.', 'E');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Cirurgia', 'Bariátrica', 'facil', 'Uma paciente de 24 anos, com IMC de 32kg/m² e sem comorbidades, procura um médico cirurgião com o desejo de realizar cirurgia bariátrica. Refere que já procurou outros cirurgiões que não quiseram realizar o procedimento. Qual seria a melhor conduta do médico diante dessa situação?', 'Indicar a cirurgia bariátrica, já que a paciente possui critérios para realizar o procedimento, após realização de pré-operatório completo.', 'Orientar a paciente que ela não possui critérios para indicação da cirurgia e que deve iniciar medidas clínicas para a perda de peso.', 'Orientar a paciente que ela deve realizar exames laboratoriais e que, se estes revelarem que ela é portadora de diabetes. terá indicação de realizar a cirurgia.', 'Orientar a paciente sobre os riscos da cirurgia e que esta deve ser indicada somente para pacientes com IMC maior que 45kg/m²', 'Orientar a paciente que ela não possui critérios para indicação da cirurgia, porém, caso a paciente insista em seu desejo, o médico deve realizar o procedimento, respeitando a vontade dela.', 'B');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Cirurgia', 'Dermatologia', 'media', 'Um paciente de 27 anos possui uma lesão pigmentada e assimétrica, de aproximadamente 7mm, localizada em pelo do dorso. Procurou um médico que indicou biópsia. Qual é o melhor tipo de biópsia a ser indicada para esse paciente?', 'Biópsia incisional.', 'Biópsia excisional.', 'Biópsia em shaving superficial.', 'Biópsia com agulha fina.', 'Biópsia com agulha grossa.', 'B');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Cirurgia', 'Coloproctologia', 'dificil', 'Levado por um vizinho, um paciente de 68 anos, previamente lúcido, foi admitido em um hospital com rebaixamento do nível de consciência. Durante a investigação, foi sugerido provável diagnostico de sepse grave de foco abdominal devido a uma diverticulite aguda perfurada. O paciente permaneceu torporoso e, após a equipe tentar entrar em contato com a família, sem sucesso, o paciente foi encaminhado à cirurgia. O médico cirurgião indicou uma retossigmoidectomia à Hartmann - com colostomia terminal. Qual é a melhor conduta diante dessa situação?', 'Proceder com a cirurgia, já que se trata de cirurgia de urgência, com risco ao paciente, necessitando que seja feita uma colostomia para melhor tratamento dele, independente da opinião da família.', 'Aguardar a chegada da família para iniciar a cirurgia, já que eles devem decidir sobre a confecção ou não de um estoma.', 'Proceder com a cirurgia sem a confecção de estoma, pelo risco de não haver aceite família quanto a esse procedimento, mesmo que seja a melhor estratégia par ao paciente.', 'Orientar o acompanhante sobre o quadro e solicitar que ele decida sobre a confecção ou não do estoma para o paciente, já que está acompanhando o paciente.', 'Proceder com a cirurgia a realizar a lavagem e drenagem da cavidade para realizar posteriormente, uma nova abordagem cirúrgica, devido à ausência da família.', 'A');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Cirurgia', 'Torácica', 'media', 'Um paciente com uma massa em ápice pulmonar direito apresenta dor e perda de força em ombro e braço direitos, além de ptose palpebral, miose e anidrose ipsilateral. A síndrome que inclui todos esses sinais e sintomas é denominada.', 'síndrome de Claude Bernard-Horner.', 'síndrome da veia cava superior.', 'síndrome de Kaposi.', 'síndrome de Pancoast.', 'síndrome do desfiladeiro torácico.', 'D');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Cirurgia', 'Plástica', 'media', 'Os pacientes submetidos à ressecção cirúrgica de lesões extensas de pele, geralmente, necessitam de retalho ou enxerto como auxílio no fechamento do defeito. Assinale a alternativa que caracteriza corretamente as técnicas empregadas nessas situações.', 'Enxerto que é um segmento de pele, sem o subcutâneo, com suprimento vascular próprio, rotacionado par a área doadora.', 'Enxerto que é um pedaço de pele retirado de uma área corpórea e transferido para outra área com um pedículo de comunicação entre as áreas.', 'Retalho que é um segmento da pele e subcutâneo com suprimento vascular próprio, que será movido de uma área (doadora) para outra (receptora), existindo uma comunicação do retalho com a área doadora por meio de um pedículo.', 'Retalho que é um segmento de pele e subcutâneo sem nenhum pedículo de comunicação entre eles. Só mais tardiamente desenvolve vascularização própria, restabelecendo, assim, um novo suprimento sanguíneo.', 'Geralmente o enxerto (área doadora) é retirado de pele adjacente, em continuidade com o defeito, obtendo assim melhores resultados.', 'C');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Cirurgia', 'Gastroenterologia', 'media', 'Assinale a alternativa que NÃO corresponde a uma indicação usual de intervenção por CPRE (Colangiopancreatografia Retrógrada Endoscópica)', 'Coledocolitíase.', 'Colangite.', 'Estenose de colédoco distal e dilatação proximal.', 'Pancreatite biliar com cálculo impactado na via biliar.', 'Colelitíase.', 'E');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Cirurgia', 'Geral', 'dificil', 'Em um reparo cirúrgico aberto de hérnia inguinal, é importante que o cirurgião tenha conhecimento de anatomia e habilidade para preservar estruturas importantes encontradas na região do canal inguinal. No caso de pacientes homens, quais são os nervos (com suas respectivas funções) que devem ser cuidadosamente dissecados e preservados nesse procedimento?', 'Ramo genital do nervo genitofermoral (sensibilidade lateral do escroto); nervo ilio-hipogástrico (sensibilidade da região inguinal e púbis / base do pênis); nervo ilioinguinal (sensibilidade de região medial superior da coxa).', 'Ramo femoral do nervo genitofemoral (sensibilidade lateral da perna); nervo ilioinguinal (sensibilidade de região medial superior da coxa); nervo obturador (motricidade - adução da perna).', 'Nervo obturador (motricidade - adução da perna) e nervo cutâneo femoral lateral (sensibilidade de região lateral superior da coxa).', 'Nervo femoral (motricidade e sensibilidade da perna); nervo ilio-hipogástrico (sensibilidade de região medial superior da coxa); nervo ilioinguinal (sensibilidade da região inguinal e púbis / base do pênis).', 'Ramo genital do nervo genitofemoral (sensibilidade lateral do escroto); nervo obturador (motricidade - adução da perna); nervo ilioguinal (sensibilidade da região inguinal e púbis / base do pênis).', 'A');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Cirurgia', 'Gastroenterologia', 'media', 'Um paciente de 57 anos possui uma lesão esofágica estenosante e não transponível ao aparelho de endoscopia em nível de esôfago médio/distal, com biópsia revelando carcinoma epidermoide escamoso. O paciente iniciará tratamento com radioterapia e quimioterapia neoadjuvantes, porém está com ingesta praticamente nula de alimentos há vários dias. Qual é a melhor estratégia cirúrgica de via alimentar a ser indicada para esse paciente?', 'Realizar uma gastrostomia endoscópica', 'Realizar uma gastrostomia laparoscópica.', 'Realizar uma jejunostomia.', 'Realizar uma ileostomia.', 'Realizar uma esofagostomia.', 'C');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Cirurgia', 'Urologia', 'facil', 'Um paciente de 74 anos recebeu o diagnóstico de HPB (Hiperplasia Prostática Benigna), e o médico indicou duas medicações para o tratamento dessa afecção e seus sintomas: a tansulosina e a finasterida. Qual é a melhor explicação que pode ser dada sobre o efeito desses compostos ao paciente?', 'A tansulosina atua diminuindo o volume prostático, e a finasterida contribui para o relaxamento da musculatura na saída da bexiga: ambas facilitam a saída do jato urinário.', 'A tansulosina contribui par ao relaxamento da musculatura na saída da bexiga, e a finasterida atua diminuindo o volume prostático; ambas facilitam a saída do jato urinário.', 'A tansulosina contribui para a contração do corp da bexiga, e a finasterida age relaxando a musculatura na saída da bexiga; ambas facilitam a saída do jato urinário.', 'A tansulosina atual na contração da musculatura da próstata, e a finasterina contribui no aumento da contração da musculatura detrusora da bexiga; ambas facilitam a saída do jato urinário.', 'A tansulosina contribui para o relaxamento da musculatura na saída da bexiga, e a finasterida atua relaxando a musculatura prostática; ambas facilitam a saída do jato urinário.', 'B');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Cirurgia', 'Gastroenterologia', 'media', 'Durante uma cirurgia de abordagem para ressecção da cabeça do pâncreas, qual das estruturas vasculares a seguir está presente nessa região e é de suma importância para o paciente, devendo-se, por isso, realizar o máximo de esforço para que seja preservada?', 'Artéria mesentérica inferior.', 'Artéria gastroduodenal.', 'Artéria pancreatoduodenal posterior.', 'Artéria mesentérica superior.', 'Artéria pancreatoduodenal superior.', 'D');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Cirurgia', 'Urologia', 'media', 'Um paciente chega ao hospital com febre há 2 dias e dor em região lombar direita. Ao realizar exames, recebe o diagnóstico de ureterolitiase obstrutiva à direita, com hidronefrose ipsilateral, alteração significativa da função renal e infecção associadas com sinais de sepse. Qual é a melhor abordagem para esse paciente?', 'Administração de anti-inflamatório não esteroidais e analgésicos e realização de litotripsia ureteroscópica.', 'Compensação clínica sintomática, administração de antibióticos e litotripsia extracorpórea - por ser o método menos invasivo para o paciente nesse momento.', 'Analgesia, prescrição de alfa-bloqueadores, como a doxazosina, e litotripsia ureteroscópica de urgência.', 'Analgesia e reposição volêmica, anti-inflamatórios não esteroidais e, por cistoscopia, descompressão do trato urinário superior à direita com urgência, além de inserção de cateter ureteral (duplo j) retrógrado ou por inserção de nefostomia percutânea.', 'Compensação clínica sintomática, administração de antibióticos e, por cistoscopia, descompressão do trato urinário superior à direita com urgência, além de inserção de cateter ureteral (duplo j) retrógrado ou por inserção de nefrostomia percutânea.', 'E');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Cirurgia', 'Neurologia', 'media', 'Um paciente, vítima de acidente automobilístico, chega ao hospital trazido pela equipe atendimento pré-hospitalar já intubado, hemodinamicamente instável e anisocórico. Na admissão, foi realizada tomografia computadorizada de crânio que mostrou imagem hiperdensa no encéfalo, próximo à calota craniana, em região parietal, de aspecto côncavo-convexo e com importante desvio de linha média. Qual é o diagnóstico e o tratamento mais indicado esse paciente?', 'Hematoma epidural / Abordagem cirúrgica.', 'Hematoma subdural / Abordagem cirúrgica.', 'Hematoma intraparenquimatoso / Monitorização da pressão intracraniana.', 'Lesão axonal difusa / Abordagem cirúrgica.', 'AVC isquêmico / Manitol endovenoso.', 'B');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Cirurgia', 'Anestesiologia', 'media', 'Um paciente foi submetido a uma cirurgia abdominal, e o anestesia introduziu um cateter peridural par analgesia. O paciente se encontra na UTI utilizando heparina de baixo peso molecular como anticoagulação profilática, e a enfermeira questiona o médico plantonista se pode retirar o cateter. Nesse caso, qual é a conduta mais adequada e a respectiva justificativa?', 'Retirar o cateter, já que a anticoagulação profilática não interfere no risco para hematoma epidural.', 'Manter o cateter e solicitar orientação ao anestesista quanto ao momento da retirada, devido ao risco da hematoma epidural.', 'O cateter poderá ser removido se a última dose de heparina tiver sido realizada há mais de 3h.', 'Manter o cateter e aguardar, pelo menos, 48h, após a última dose da heparina para remover o cateter.', 'Manter o cateter por, pelo menos 24h, após a última dose de heparina devido ao risco de hematoma subdural.', 'B');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Clínica Médica', 'Cardiologia', 'media', 'Um homem de 46 anos procura atendimento médico de urgência com queixa de palpitações e dor torácica do tipo aperto, de inicio hà aproximadamente 1 hora. Seus sinais vitais são aferidos: - PA: 70x40mmHg (Pressão arterial): - FC: 190bpm (Frequência cardiaca); - Sat02: 88% (Saturação de oxigênio). E seu eletrocardiograma (ECG) é o seguinte: Nesse caso, conforme as informações apresentadas, é correto afirmar que', 'se trata de uma taquicardia supraventricular, e o uso da manobra vagal modificada está indicado', 'a avaliação de um especialista deve ser prioritária.', 'o uso de atropina 1mg endovenosa (EV) está indicado.', 'a cardioversão elétrica sincronizada está indicada', 'um estudo eletrofisiológico deve ser realizado antes de qualquer intervenção terapéutica', 'D');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Clínica Médica', 'Cardiologia', 'media', 'Uma mulher de 78 anos procura atendimento médico de urgência com queixa de dor no estômago e náuseas há aproximadamente 2 horas, obtendo-se o seguinte ECG da paciente: Nesse caso, considerando as apresentadas, é correto afirmar que', 'se trata de uma colecistite aguda, e a paciente deve ser internada aos cuidados da cirurgia geral.', 'uma opção de reperfusão coronariana deve ser indicada imediatamente.', 'o uso de morfina e nitrato está indicado', 'uma endoscopia digestiva alta é prioritária.', 'a paciente deve ser encaminhada para avaliação ambulatorial com um cardiologista.', 'B');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Clínica Médica', 'Cardiologia', 'media', 'Sobre o fenômeno (ou síndrome) de Eisenmenger, assinale a alternativa correta.', 'E muito comum nos casos de infarto agudo do miocárdio.', 'Uma das principais caracteristicas clínicas é a cianose de extremidades', 'É comum nos casos de comunicação interventricular, sendo caracterizado pelo shunt esquerda-direita.', 'Foi descrito por Frederick Eisenmenger em 1973 em estudo publicado após análise de 52 casos de cardiopatias congénitas cianóticas', 'O tratamento com inibidores da enzima conversora de angiotensina (ECA) está indicado', 'B');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Clínica Médica', 'Cardiologia', 'media', 'Em relação à classificação da insuficiência cardiaca, segundo a American Heart Association (AHA), é correto afirmar que', 'um homem de 56 anos, com fração de ejeção (FE) de 36% no ecocardiograma, é considerado classe A', 'uma mulher caucasiana de 61 anos, com hipertensão artenal e sem alterações no ECG e/ou no ecocardiograma, é considerada classe C', 'uma mulher de 42 anos, portadora de miocardiopatia puerperal, com FE de 18% e quadro clinico refratário ao tratamento clinico otimizado, é considerada classe D.', 'a presença de alterações cardiacas estruturais, sem sintomas de insuficiência cardiaca, é considerada classe C.', 'a presença de dispneia aos pequenos esforços é considerada classe III.', 'C');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Clínica Médica', 'Cardiologia', 'media', 'Um homem de 75 anos, portador de estenose mitral grave, procura atendimento médico com queixa de dispneia e lipotimia. Sobre o exame fisico desse paciente, é correto afirmar que', 'a presença de estalido de abertura precoce é frequente.', 'a hipofonese de B1 é comum.', 'sinais de insuficiência cardiaca esquerda são comuns', 'o exame fisico costuma ser normal', 'a hipofonese de B2 é caracteristica', 'A');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Clínica Médica', 'Endocrinologia', 'media', 'Um homem de 24 anos procura atendimento médico com queixa de falta de ar e boca seca. Sua glicemia capilar é aferida, 467mg/dL. Nega doenças prévias ou uso de medicações. Diante desse caso, assinale a alternativa correta.', 'A presença de acidose respiratória é comum nesses casos', 'O uso de insulina de ação rápida pode levar ao aumento do potássio sérico', 'A restrição hidrica esta indicada nesse caso', 'A dosagem da hemoglobina glicosilada é essencial para determinar a conduta correta', 'Os titulos de anticorpos antidescarboxilase do ácido glutâmico (GADA) são importantes para o diagnóstico de LADA (latent autoimmune diabetes of the adult)', 'E');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Clínica Médica', 'Endocrinologia', 'media', 'Em relação ao hipotireoidismo, é correto afirmar que', 'é muito importante solicitar tireoglobulina sérica na avaliação inicial de nódulos de tireoide.', 'a dosagem do T3 reverso (rT3), na avaliação de função tireoideana, é essencial.', 'não devem ser repetidos exames de autoanticorpos [antitireoperoxidase (Anti-TPO) e/ou antitireoglobulina] no seguimento de pacientes com hipotireoidismo por tireoidite de Hashimoto com exame anterior positivo.', 'é essencial a dosagem de tri-iodotironina (LT3), isolada ou em associação com levotiroxina (LT4), no tratamento de hipotireoidismo', 'a dosagem de marcadores moleculares, na avaliação inicial de pacientes com nódulo de tireoide, é muito importante para o plano terapêutico', 'C');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Clínica Médica', 'Neurologia', 'facil', 'Um homem de 27 anos é levado ao pronto atendimento após queda de n��vel elevado. Depois de assegurar uma via aérea pérvia, a imobilização da coluna cervical e a checagem dos sinais vitais, a resposta ao exame neurológico é a seguinte: flexão normal, sons irreconhecíveis e abertura ocular (todos após estímulos dolorosos). Diante do exposto, assinale a alternativa correta.', 'A tomografia de crânio é importante para determinar a escala de coma de Glasgow desse paciente.', 'O homem apresenta um traumatismo crânioencefálico (TCE) leve e pode ser liberado com segurança.', 'A avaliação do médico neurocirurgião é essencial para a definição entre TCE leve, moderado ou grave.', 'O homem apresenta 8 pontos na escala de coma de Glasgow', 'Uma tomografia de coluna cervical é importante para determinar a escala de coma de Glasgow desse paciente.', 'D');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Clínica Médica', 'Emergência', 'facil', 'Um homem de 63 anos, recebendo medicação para dor epigástrica na sala de observação do pronto atendimento, apresenta um colapso ao levantar-se da poltrona. Qual é a conduta imediata mais adequada nesse caso?', 'Realizar um eletrocardiograma de 12 derivações.', 'Checar a responsividade.', 'Aplicar 1mg de epinefrina EV.', 'Iniciar compressões torácicas.', 'Solicitar avaliação da cardiologia.', 'B');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Clínica Médica', 'Dor', 'media', 'Sobre a dor, assinale a alternativa correta.', 'Os nociceptores são responsáveis pela transdução.', 'A dor neuropática tem como principal característica a melhora à noite e nos meses mais frios.', 'A migrânea é um exemplo clássico de dor somática superficial.', 'A pregabalina atua nos receptores opioides do sistema nervoso central, principalmente μ1 e μ2.', 'A presença de injúria tecidual é essencial para a explicação do fenômeno doloroso.', 'A');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Clínica Médica', 'Neurologia', 'dificil', 'Um homem de 62 anos é levado ao hospital com história de perda de força em dimídio direito, afasia e desvio de rima labial, de início há aproximadamente 40min. Após a realização de uma tomografia computadorizada de crânio (normal, com laudo oficial do radiologista), seus sinais vitais são os seguintes: - PA: 192 x 124 mmHg - Glicemia capilar: 88 mg/dL - NIHSS: 18 pontos Nesse caso, qual é a conduta mais adequada?', 'Chamar o neurologista de plantão.', 'Uso de um comprimido de nifedipina 20 mg sub lingual.', 'Uso de 300 mg de ácido acetilssalicílico (AAS) por via oral.', 'Uso de 20 mg de rivaroxabana por via oral', 'Controle da pressão arterial com medicação endovenosa - alvo abaixo de 180x100 mmHg - seguido de trombólise com alteplase 0,9 mg/Kg, na ausência de contraindicações.', 'A');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Clínica Médica', 'Pneumologia', 'facil', 'Uma mulher de 71 anos, com histórico de DPOC, procura atendimento médico com queixa de falta de ar. O resultado da gasometria arterial é o seguinte: - pH: 7,25; - CO2: 62; - BIC: 35. Nesse caso, é correto afirmar que', 'se trata de uma alcalose metabólica.', 'a gasometria está normal.', 'se trata de uma acidose metabólica.', 'se trata de uma acidose respiratória crônica, provavelmente agudizada.', 'está indicada reposição de bicarbonato EV.', 'D');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Clínica Médica', 'Neurologia', 'media', 'Sobre a neuromielite óptica, assinale a alternativa correta.', 'É uma forma de apresentação da Esclerose Múltipla.', 'Pleocitose é bastante especifica para e diagnóstico.', 'Também é conhecida como síndrome de Devic.', 'A presença de paralisia espástica é frequente.', 'A tomografia de crânio apresenta alterações especificas.', 'C');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Clínica Médica', 'Reumatologia', 'media', 'Um homem de 56 anos procura atendimento médico por crise de artrite gotosa, apresentando sinais clássicos de podagra. Sobre o tratamento para esse caso, é correto afirmar que', 'a colchicina está contraindicada se o paciente for portador de doença renal crônica e estiver em uso contínuo de carvedilol.', 'o uso de corticosteroides por via oral não é recomendado.', 'o alopurinol está indicado durante a crise aguda.', 'o uso de inibidores de interleucina-1 é considerado tratamento de primeira escolha.', 'a prednisona está indicada na dose de 5mg por via oral em dias alternados.', 'A');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Clínica Médica', 'Hematologia', 'media', 'São considerados anticoagulantes, por bloqueio do fator Xa, EXCETO', 'rivaroxabana.', 'edoxabana.', 'enoxaparina.', 'dabigatrana.', 'apixabana.', 'D');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Clínica Médica', 'Reumatologia', 'media', 'Em relação à febre reumática, é correto afirmar que', 'a coreia de Sydenham é considerada um critério maior para o diagnóstico, sendo muito frequente na apresentação inicial (80-90%)', 'a dor articular (artralgia) é considerada um critério menor para o diagnóstico.', 'a presença de febre baixa é essencial para o diagnóstico.', 'a faixa etária mais acometida é de 40 - 50 anos', 'o uso de corticosteroides está indicado na profilaxia da febre reumática.', 'B');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Clínica Médica', 'Urologia', 'media', 'Um homem de 54 anos procura atendimento médico com queixa de dor ao urinar e urgência miccional. O diagnóstico de prostatite é estabelecido e a urocultura mostra crescimento de pseudomonas aeruginosa. Diante do exposto, assinale a alternativa correta.', 'A pseudomonas aeruginosa é responsável pela maioria dos casos de prostatite bacteriana aguda.', 'O quadro clínico geralmente é pouco sintomático.', 'A incidência de neoplasia de próstata é comum nesses casos.', 'A macrodantina é o antimicrobiano de escolha nesses casos.', 'O uso de ertapenem está contraindicado.', 'E');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Clínica Médica', 'Oncologia', 'facil', 'Qual neoplasia maligna é responsável pelo maior número de óbitos no Brasil?', 'Neoplasia de pulmão.', 'Neoplasia de intestino.', 'Neoplasia de mama.', 'Neoplasia de pele.', 'Neoplasia de colo de útero.', 'A');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Clínica Médica', 'Reumatologia', 'media', 'Em relação à artrite reumatoide, é correto afirmar que', 'os anti-inflamatórios não esteroidais (AINE) são considerados drogas modificadoras de doença.', 'o diagnóstico deve ser confirmado pela dosagem do fator reumatoide.', 'a presença de marcadores agudos de inflamação (proteína C reativa ou velocidade de hemossedimentação) adiciona 5 pontos aos critérios mais recentes de diagnóstico.', 'o metotrexato é considerado droga de primeira escolha.', 'o uso de corticosteroides intra-articulares está indicado na maioria dos casos.', 'D');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Clínica Médica', 'Cardiologia', 'media', 'Sobre o carvedilol, assinale a alternativa correta.', 'Sua dose máxima recomendada é 100 mg duas vezes ao dia.', 'É considerado uma medicação de primeira escolha nos casos de hipertensão arterial sistêmica.', 'Está contraindicado nos pacientes com bloqueio atrioventricular avançado.', 'Está indicado nos casos de insuficiência cardíaca descompensada, principalmente nos pacientes com perfil frio e úmido.', 'Pode ser associado ao propranolol nos pacientes portadores de fibrilação atrial.', 'C');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Pediatria', 'Oncologia', 'dificil', 'Sobre a predominância das neoplasias em pediatria, de acordo com a idade e o sitio primário, assinale a alternativa correta.', 'Entre os linfomas, tanto o de Hodgkin quanto o de não Hodgkin, são bastante predominantes em menores de 1 ano.', 'Entre as leucemias, a leucemia mieloide crônica juvenil é mais predominante na adolescência e é rara em menores de 3 anos.', 'Das neoplasias de sistema nervoso central, o astrocitoma cerebelar está entre as mais predominantes em maiores de 3 anos.', 'Das neoplasias com sitio primário em cabeça e pescoço, as mais predominantes, em todas as faixas etárias são o sarcoma de partes moles e o linfoma.', 'O neuroblastoma e o tumor de Wilms são as neoplasias de sitio abdominal mais predominantes em adolescentes.', 'C');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Pediatria', 'Gastroenterologia', 'facil', 'Na fase mais inicial da apendicite aguda, qual é a tríade sintomática mais característica entre as seguintes?', 'Dor em fossa iliaca direita. febre alta prostração.', 'Dor epigástrica intensa febre moderada e vômitos.', 'Dor abdominal difusa, febre alta e anorexia.', 'Dor periumbilical, febre alta e vómitos.', 'Dor periumbilical, febre moderada e anorexia.', 'E');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Pediatria', 'Dermatologia', 'facil', 'Você está atendendo uma criança de 6 anos com queixa de lesões de pele, com prurido intenso, que ocorre principalmente à noite, há alguns dias. O paciente não apresentou febre ou qualquer outro sintoma. As lesões são papulovesiculares eritematosas, localizadas em espaços interdigitais, axilas, punhos, regiões glútea e genital. Os pais estão presentes na consulta, e a mãe refere os mesmos sintomas, com lesões semelhantes, mas com presença de lesões em túnel. O pai está assintomático e sem lesões. Qual é a conduta adequada em relação ao paciente e aos seus pais?', 'Prescrever tratamento para a criança e para os familiares, independentemente da presença de sintomas.', 'Prescrever o tratamento para a criança e para os familiares sintomáticos.', 'Prescrever o tratamento apenas para a criança, que é sua paciente.', 'Prescrever o tratamento para a criança e para a mãe.', 'Prescrever o tratamento para a criança e indicar avaliação com dermatologista para a mãe.', 'A');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Pediatria', 'Imunização', 'media', 'Em relação à vacina tríplice viral, oferecida pelo Programa Nacional de Imunizações, assinale a alternativa correta.', 'A dose zero, dada em situação epidemiológica de risco para sarampo ou rubéola, não é considerada válida para a cobertura vacinal de rotina.', 'E contraindicada para gestantes, exceto em situações de surto de sarampo caxumba ou rubéola.', 'É contraindicada para crianças abaixo dos 12 meses de idade, mesmo em situações de surto de sarampo, caxumba ou rubéola.', 'Mulheres em idade fértil devem evitar a gravidez até pelo menos 1 ano após a vacinação.', 'Pessoas comprovadamente portadoras de alergia á proteína do leite de vaca não podem receber vacina tríplice viral.', 'A');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Pediatria', 'Pneumologia', 'media', 'Uma criança em idade escolar está em antibioticoterapia, há 48 horas, por pneumonia bacteriana. Ela é levada ao hospital para reavaliação, pois não teve melhora da curva térmica, está prostrada e com perda de apetite. Ao exame físico, apresenta diminuição do murmúrio vesicular em base esquerda e tem bom padrão respiratório, apesar de manter uma posição antálgica em escoliose. Qual, entre as seguintes, é a conduta mais indicada no momento?', 'Realizar radiografia de tórax.', 'Trocar antibiótico e reavaliar em 48 a 72 horas.', 'Manter antibiótico e aguardar completar 72 horas de tratamento para uma reavaliação mais adequada.', 'Realizar tomografia de tórax com contraste.', 'Associar um segundo antibiótico ao esquema inicial, para cobertura de germes atípicos, e reavaliar em 48 horas.', 'A');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Pediatria', 'Infectologia', 'facil', 'As infecções sexualmente transmissiveis são um grave problema de saúde pública no Brasil, devendo-se ter especial atenção à população adolescente. Considerando somente as infeccões adquiridas, qual, das seguintes, pode transmitida de outras formas além da sexual?', 'Sifilis.', 'Hepatite B.', 'Gonorreia.', 'Linfogranuloma venéreo.', 'Cancro mole.', 'A');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Pediatria', 'Reumatologia', 'media', 'Um menino de 3 anos apresenta quadro febril há 6 dias e é levado para avaliação. Durante a consulta, são identificadas outras alterações: exantema polimorfo difuso, conjuntivite sem exsudato, mucosite oral, edema distal dos membros e adenomegalia cervical unilateral. Suspeitando-se de Doença de Kawasaki, qual dos sinais ou sintomas apresentados pelo paciente é considerado o critério mandatório para diagnostico?', 'Febre.', 'Exantema.', 'Conjuntivite.', 'Mucosite.', 'Edema distal.', 'A');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Pediatria', 'Hematologia', 'media', 'Um paciente de 5 anos, assintomático, está em consulta de puericultura. Ao exame físico, é identificado um linfonodo supraclavicular palpável. Nesse caso, está indicada a biópsia desse linfonodo?', 'Não, pois o paciente deve ser revisado em duas semanas para avaliar se houve regressão.', 'Não, pois o paciente é assintomático.', 'Sim, independentemente das características do linfonodo.', 'Sim, se o linfonodo for firmemente aderido e maior que 2 cm.', 'Sim, se o linfonodo for maior que 1 cm.', 'C');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Pediatria', 'Infectologia', 'media', 'Sobre a rubéola congênita, assinale a alternativa correta.', 'Perda auditiva é a manifestação mais comum e usualmente é condutiva bilateral, e a severidade varia de moderada a grave, com progressão ao longo do tempo', 'Cerca de 50% apresentam algum tipo de defeito cardíaco estrutural, sendo mais comuns os defeitos de septo cardíaco.', 'O risco de transmissão materno-fetal é maior nas primeiras 10 semanas de gestação, e o risco de ocorrer malformações prolonga-se até a 18ª e a 20 semana.', 'As manifestações tardias de diabete melito e das patologias da tireoide são bastante comuns, afetando mais de 1/3 dos casos ate adolescência.', 'O uso de imunoglobulina para tratamento da rubéola materna garante proteção contra infecção do feto.', 'A');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Pediatria', 'Nefrologia', 'dificil', 'A glomerulonefrite difusa aguda pós-estreptocócica e a mais comum das glomerulopatias da infância. Sobre essa glomerulonefrite, assinale a alternativa correta.', 'A dosagem do complemento sérico é obrigatória tendo seus valores diminuídos em 95 a 98% dos casos, sendo que sua normalização em 4 a 8 semanas e um marcador importante de prognóstico e diagnóstico diferencial.', 'Nas alterações urinárias, observa-se hematúria, macroscópica ou microscópica, em cerca de 95% dos casos e proteinuria, raramente em níveis nefróticos, o que na fase aguda, é um importante indicador de gravidade da nefropatia.', 'A insuficiência renal aguda é uma das complicações mais comuns, apresentando oligoanúria intensa, retenção de escórias proteicas no plasma e distúrbios hidreletrolíticos. graves. com tendência à hiperpotassemia.', 'A encefalopatia hipertensiva é uma complicação que se deve essencialmente à hipertensão e pode cursar com cefaleia, vômitos, alterações Visuais, agitação, sonolência, crise convulsiva ou coma e, ao exame de fundo de olho, observam se as alterações características de hipertensão arterial, na maioria dos casos.', 'Congestão circulatória e a complicação mais frequente, caracterizada por sinais clínicos de hipovolemia e que pode ser agravada por hipertensão, levando a insuficiência cardíaca congestiva e edema agudo de pulmão, com evidência de dano miocárdico intrínseco.', 'A');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('ENARE', 2023, 'Pediatria', 'Emergência', 'facil', 'Você está de férias, na praia, quando se depara com um afogamento. A vitima está desacordada, parece ter 16 anos, aproximadamente, e está sendo retirada da água. Ela é colocada na areia, em decúbito dorsal, você vai prestar o e atendimento inicial. Uma ambulância já foi chamada e chegará em poucos minutos. Das alternativas a seguir, qual deve ser a sua próxima atitude?', 'Estabilização de cervical.', 'Ventilação de resgate.', 'Posicionar o paciente em decúbito lateral.', 'Manobra de Heimlich.', 'Compressões abdominais.', 'C');
