INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('UNIFESP', 2025, 'Anestesiologia', NULL, 'media', 'Mulher, 27 anos de idade, 55 kg e 1,65 m, submetida a raquianestesia e anestesia geral para tratamento cirúrgico de endometriose. Durante avaliação pré-anestésica foi identificado alto risco para náusea e vômito no pós-operatório. Com objetivo de reduzir o risco de ocorrência de complicação no pós-operatório, qual é a conduta mais adequada?', 'Anestesia balanceada com propofol, remifentanil e sevoflurano.', 'Anestesia inalatória com sevoflurano e óxido nitroso.', 'Anestesia combinada com propofol e isoflurano.', 'Anestesia venosa total com propofol e remifentanil.', NULL, 'D');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('UNIFESP', 2025, 'Gastroenterologia', NULL, 'media', '75 anos de idade, apresenta dor abdominal em cólica de forte intensidade e com 36 horas de evolução, de localização difusa, acompanhado de náuseas e vômitos. Nas últimas 24 horas, nega eliminação de gases e fezes. Exame físico: REG, descorada, FC 90 bpm, PA 150 x 95 mm Hg, temperatura 37,5 °C; abdômen globoso, distendido, ruídos hidroaéreos aumentados, hipertimpanismo difuso e doloroso à palpação, mas sem sinais de irritação peritoneal. A sequência do Rx de abdome em três posições evidenciou estômago e alças intestinal difusamente dilatadas, níveis hidroaéreos e pneumocolangiograma presente. Qual é o diagnóstico mais provável?', 'Hérnia interna', 'Trombose mesentérica', 'Íleo biliar', 'Neoplasia colorretal obstrutiva', NULL, 'C');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('UNIFESP', 2025, 'Cirurgia', NULL, 'media', 'Homem, 24 anos de idade, é admitido no PS por acidente automobilístico. Exame físico: via aérea pérvia, pneumotórax esquerdo com fratura linear da 3°, 4° e 5° costelas, estável hemodinamicamente, sinais de trauma raquimedular em L3-L4, múltiplas fraturas em membros superiores e inferiores. Exames de imagem: ausência de outras lesões no abdômen e na pelve. Com relação à drenagem da via urinária, qual é a conduta mais adequada?', 'Sondagem vesical de demora.', 'Cistostomia supra-púbica.', 'Cateterismo intermitente.', 'Nefrostomia bilateral guiada por radiointervenção.', NULL, 'A');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('UNIFESP', 2025, 'Cardiologia', NULL, 'media', 'Homem, 48 anos de idade, previamente assintomático, comparece ao PS com dor torácica de forte intensidade, em aperto, com início súbito e irradiação para dorso e região interescapular, há 2 horas. Exame físico: sudoreico, FC 100 bpm, PA 190 x 100 mm Hg nos quatro membros, pulsos rítmicos e presença de sopro diastólico suave, 4+/6+, no foco aórtico. Após análise do eletrocardiograma, qual é a conduta mais adequada?', 'Cirurgia de emergência', 'Angioplastia primária da artéria culpada', 'Observação e tratamento medicamentoso', 'Acompanhamento ambulatorial', NULL, 'A');
INSERT INTO questions (institution, year, area, subarea, difficulty, question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES ('UNIFESP', 2025, 'Obstetrícia', NULL, 'media', 'Mulher, 36 anos de idade, IG 38 semanas e 3 dias, com gestação gemelar dicoriônica, apresenta diabetes mellitus gestacional. Possui bom controle glicêmico, alcançado com orientação dietética. Exames: taxas de hemoglobina glicada que variaram entre 4,9 e 5,4% e ultrassonografia recente mostrando o feto I em apresentação cefálica e peso estimado no percentil 35, e o feto II em apresentação pélvica e peso estimado no percentil 38 da curva própria para gestação gemelar. Qual é a conduta mais adequada?', 'Indução do trabalho de parto.', 'Retorno para controle em 7 dias.', 'Parto cesáreo.', 'Perfil biofísico em dias alternados até 40 semanas.', NULL, 'C');
